import React, { useState } from 'react'
import { motion } from 'framer-motion'
import { useNavigate } from 'react-router-dom'
const containerAnim = {
    hidden: {
        y: 300,
        opacity: 0,
    },
    visible: {
        y: 0,
        opacity: 1,
        transition: {
            type: "spring",
            duration: .4,
            delay: .3,
            when: "beforeChildren",
            staggerChildren: .15,
        }
    }
}
const shadowAnim = {
    hidden: {
        opacity: 0,
    },
    visible: {
        opacity: 1,
        transition: {
            when: "beforeChildren",
            duration: .4,
            delay: .04,
        }
    }
}


const Login = () => {
    const navigate = useNavigate()
    const [logReg, setLogReg] = useState(true)
    document.body.style.overflow = "hidden"
    const submitHandler = (e) =>{
        e.preventDefault()
        console.log(3223)
    }
    window.addEventListener('popstate', function(event)  {
      document.body.style.overflow = "auto"

     })

    const  exitDetailHandler = (e) => {
      const element = e.target

      if(element.classList.contains("shadow")){
        document.body.style.overflow = "auto"
        // navigate(`/${location}`)
        navigate(-1)
      }
    }
  return (
    <motion.div variants={shadowAnim} animate = "visible"
    initial = "hidden" className='shadow' onClick={exitDetailHandler}>
        <motion.div variants={containerAnim}
        animate = "visible"
        initial = "hidden" className="container">
            <form onSubmit={submitHandler}>
                <div className="form-inner">
                    <h2><span className={`${ logReg && "active-btn"}`} onClick={()=>setLogReg(true)}>Sign In </span>/<span onClick={()=>setLogReg(false)} className={`${ !logReg && "active-btn"}`}> Sing Up</span></h2>
                    {/* <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" name='name' id="name" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <input type="password" name='password' id="password" />
                    </div>
                    <button type="submit" value="LOGIN" >Login <span></span></button>  */}
                    <div className='cont log'>
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" name='name' id="name" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Email:</label>
                        <input type="email" name='email' id="email" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <input type="password" name='password' id="password" />
                    </div>
                    <button type="submit" value="LOGIN" >Login <span></span></button> 
                    </div>
                    <div className='cont reg'>
                    <div className="form-group">
                        <label htmlFor="name">Name:</label>
                        <input type="text" name='name' id="name" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Email:</label>
                        <input type="email" name='email' id="email" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <input type="password" name='password' id="password" />
                    </div>
                    <button type="submit" value="LOGIN" >Login <span></span></button> 
                    </div>

                </div>
            </form>
        </motion.div>
    </motion.div>
  )
}

export default Login
