import { useState, useRef } from "react";
import Song from "./components/Song";
import Player from "./components/Player";
import "./styles/app.scss"
import data from "./data"
import Library from "./components/Library";
import Nav from "./components/Nav";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";

function App() {
  const [songs, setSongs] = useState(data())
  const [currentSong, setCurrentSong] = useState(songs[0])
  const [isplaying, setIsplaying] = useState(false)
  const [libraryStatus, setLibraryStatus ] = useState(true)
  const audioRef = useRef(null)

  return (
    <BrowserRouter className={`App ${libraryStatus && "library-active"}`}>
            <Nav libraryStatus={libraryStatus} setLibraryStatus={setLibraryStatus}/>
      <Song isplaying={isplaying} currentSong={currentSong}/>
      <Player currentSong={currentSong} isplaying={isplaying} setIsplaying={setIsplaying} audioRef={audioRef} songs={songs} setCurrentSong={setCurrentSong} setSongs={setSongs}/>
      <Library  libraryStatus={libraryStatus} setCurrentSong={setCurrentSong} songs={songs} audioRef={audioRef} isplaying={isplaying} setSongs={setSongs}/>
    <Routes>
      <Route path="/" element={<Nav libraryStatus={libraryStatus} setLibraryStatus={setLibraryStatus}/>}/>


    </Routes>

    </BrowserRouter>
  );
}

export default App;
