import React from 'react'
import { useEffect } from 'react';
import { useRef } from 'react'

const CustomCursor = () => {
    const secondaryCursor = React.useRef(null);
  const mainCursor = React.useRef(null);
  const positionRef = React.useRef({
    mouseX: 0,
    mouseY: 0,
    destinationX: 0,
    destinationY: 0,
    distanceX: 0,
    distanceY: 0,
    key: -1,
  });
      useEffect(() => {
        document.addEventListener("mousemove", (event) => {
          const { clientX, clientY } = event;

          const mouseX = clientX;
          const mouseY = clientY;

          positionRef.current.mouseX =
            mouseX - secondaryCursor.current.clientWidth / 2;
          positionRef.current.mouseY =
            mouseY - secondaryCursor.current.clientHeight / 2;
          mainCursor.current.style.transform = `translate3d(${mouseX -
            mainCursor.current.clientWidth / 2}px, ${mouseY -
            mainCursor.current.clientHeight / 2}px, 0)`;
        });

        return () => {};
      }, []);
  return (
    <div>
        <div ref={mainCursor} className="main-cursor"></div>
        <div ref={secondaryCursor} className="secondary-cursor"></div>
    </div>
  )
}

export default CustomCursor
