import React, {useState,useEffect} from 'react'
import { motion } from 'framer-motion'
import { useNavigate } from 'react-router-dom'
import { containerAnim, shadowAnim } from '../animations'
import { listSongDetails,createSongReview } from '../actions/songActions'
import { useDispatch, useSelector } from 'react-redux'
import { SONG_CREATE_REVIEW_RESET } from '../constants/songConstants'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faArrowDown} from "@fortawesome/free-solid-svg-icons"
import Loader from './Loader'
import Message from './Message'
const Reviews = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()
    const [errormes, setErrormes] = useState(true)
    document.body.style.overflow = "hidden"
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState("")

    const songDetails = useSelector(state => state.songDetails)
    const {currentSong, error, loading} = songDetails    

    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = songDetails    

    const songReviewCreate = useSelector(state => state.songReviewCreate)
    const {
      loading: loadingSongReview,
      error : errorSongReview,
      success: successSongReview,

    } = songDetails    
    console.log(currentSong)
    const  exitDetailHandler = (e) => {
      const element = e.target

      if(element.classList.contains("rshadow")){
        document.body.style.overflowY = "auto"

        navigate(`/`)
        // navigate(-1)
      }
    }
    const submitHandler = (e) => {
      e.preventDefault()
    }
    useEffect(()=>{
      if(successSongReview){
        setRating(0)
        setComment("")
        dispatch({type: SONG_CREATE_REVIEW_RESET})
      }
    },[navigate, dispatch, successSongReview])
  return (
    <motion.div variants={shadowAnim} animate = "visible"
    initial = "hidden" className='rshadow' onClick={exitDetailHandler}>
        {successSongReview && <Message color={"white"} errormes={errormes} setErrormes={setErrormes}>Review Submited</Message>}
        {errorSongReview && <Message color={"white"} errormes={errormes} setErrormes={setErrormes}>{errorSongReview}</Message>}

        {loadingSongReview ? <Loader/> :
        <motion.div variants={containerAnim}
        animate = "visible"
        initial = "hidden" className="rcontainer">
          <form onSubmit={submitHandler} className="comment_rating">
          <textarea value={comment}  onChange={(e)=>setComment(e.target.value)} name="comment" id="comment"  rows="3" maxLength={200}>

              </textarea>
            <div className="rating_btn">
            <label className="select" htmlFor="slct">
              <select id="slct" required="required" value={rating} onChange={e=>setRating(e.target.value)}>
                <option value="" disabled="disabled" selected>Rating</option>
                <option value={1}>1 - Poor</option>
                <option value={2}>2 - Fair</option>
                <option value={3}>3 - Good</option>
                <option value={4}>4 - Very Good</option>
                <option value={5}>5 - Excelent</option>
              </select>
              <FontAwesomeIcon className='arrow' icon={faArrowDown} />
            </label>
            <button disabled={loadingSongReview}  type="submit" value="LOGIN" >Login <span className='weve'></span></button> 
            </div>

          </form>
          <div className="all_ratings">
            {currentSong.reviews.map(review=>
              <div className="review" key={review._id}>
                <div className="name_date"><strong>{review.name}</strong><p className='date'>{review.createdAt.substring(0,10)}</p></div>

                <p>{review.comment}</p>
              </div>
              )}
          </div>
        </motion.div>
        }
    </motion.div>
  )
}

export default Reviews
