import React  from 'react'

const LibrarySong = ({song,setCurrentSong,songs, audioRef, isplaying, }) => {
    const songSelectHandler = () => {
        const selectedSong = songs.find(state=>state.id ===song.id)

        setCurrentSong(selectedSong)
        const nevSongs = songs.map((state)=> {
            if(state.id ===song.id){
                return {...song, active:true}
            }
            else{
                return {...song, active:false}
            }
        })
        if(isplaying){
            const playPromise = audioRef.current.play()
            if(playPromise !==undefined){
                playPromise.then((audio) => {
                    audioRef.current.play()
                })
            }
        }
    }
  return (
    <div className={`library-song ${song.active ? "selected" : ""}`} onClick={songSelectHandler}>
        <img src={song.cover} alt={song.name} />
        <div className="song-description"><h3>{song.name}</h3>
        <h4>{song.artist}</h4></div>

    </div>
  )
}

export default LibrarySong
