import { useState, useRef } from "react";
import Song from "./components/Song";
import Player from "./components/Player";
import "./styles/app.scss"
import data from "./data"
import Library from "./components/Library";
import Nav from "./components/Nav";
function App() {
  const [songs, setSongs] = useState(data())
  const [currentSong, setCurrentSong] = useState(songs[0])
  const [isplaying, setIsplaying] = useState(false)
  const [libraryStatus, setLibraryStatus ] = useState(true)
  const audioRef = useRef(null)

  return (
    <div >
      <Nav libraryStatus={libraryStatus} setLibraryStatus={setLibraryStatus}/>
      <Song className={`App ${libraryStatus && "library-active"}`} currentSong={currentSong}/>
      <Player className={`App ${libraryStatus && "library-active"}`} currentSong={currentSong} isplaying={isplaying} setIsplaying={setIsplaying} audioRef={audioRef} songs={songs} setCurrentSong={setCurrentSong} setSongs={setSongs}/>
      <Library className={`App ${libraryStatus && "library-active"}`} libraryStatus={libraryStatus} setCurrentSong={setCurrentSong} songs={songs} audioRef={audioRef} isplaying={isplaying} setSongs={setSongs}/>
    </div>
  );
}

export default App;
