import { motion } from 'framer-motion';
const Song = ({currentSong, isplaying}) => {
    const [speed, setSpeed] = useState(3)
    const [an, setAn] = useState({ repeat: 3, duration: speed ,ease: "linear", })
    const aa = useRef(null)
    useEffect(()=>{
        if(!isplaying) {
            setSpeed(3)

        }

        else{
            setSpeed(0)
        }
        setAn({ repeat: Infinity, duration: 3 ,ease: "linear", })
        console.log(aa.current)
    }, [isplaying])
  return (
    <div className='song-container'>
        {isplaying ? 
        <motion.div ref={aa} animate={{ rotate: 360 }}
        transition={an} className='img-container'>
      <img  src={currentSong.cover} alt={currentSong.name} />
      </motion.div>
      :<div animate={{ rotate: 360 }}
      transition={an} className='img-container'>
    <img  src={currentSong.cover} alt={currentSong.name} />
    </div>
        }
        <h2>{currentSong.name}</h2>
        <h3>{currentSong.artist}</h3>
    </div>
  )
}

export default Song
