import React from 'react'

const Song = ({currentSong}) => {
  return (
    <div className={`App song-container ${libraryStatus && "library-active"}`} >
        <img src={currentSong.cover} alt={currentSong.name} />
        <h2>{currentSong.name}</h2>
        <h3>{currentSong.artist}</h3>
    </div>
  )
}

export default Song
