import React, { useEffect, useRef } from 'react'
import { motion,useAnimation } from 'framer-motion';
const Song = ({libraryStatus, currentSong, isplaying}) => {
    const control = useAnimation()
    const t = useRef(null)
    useEffect(()=>{
        if(isplaying){
            control.start({rotate: 36000000, transition: { duration: 500000 ,ease: "linear"}})
        }
        else{
            control.stop()
        }
        console.log(t)
    },[isplaying, control])
  return (
    <div className={`song-container ${libraryStatus && "library-active"}`}>
        <a href='/red' className="song_review">
        <motion.div ref={t} animate = {control} className='img-container'>
        <img  src={currentSong.cover} alt={currentSong.name} />
        </motion.div>
        <div className='review'>
            <h3>Reviews</h3>
        </div>
        </a>

        <h2>{currentSong.name}</h2>
        <h3>{currentSong.artist}</h3>
    </div>
  )
}

export default Song
