import React, {useState} from 'react'
import { motion } from 'framer-motion'
import { useNavigate } from 'react-router-dom'
import { containerAnim, shadowAnim } from '../animations'
import { listSongDetails,createSongReview } from '../actions/songActions'
import { useDispatch, useSelector } from 'react-redux'
import { SONG_CREATE_REVIEW_RESET } from '../constants/songConstants'
const Reviews = () => {
    const navigate = useNavigate()
    document.body.style.overflow = "hidden"
    const [rating, setRating] = useState(0)
    const [comment, setComment] = useState("")

    window.addEventListener('popstate', function(event)  {
      // document.body.style.overflow = "auto"

     })

    const  exitDetailHandler = (e) => {
      const element = e.target

      if(element.classList.contains("rshadow")){
        document.body.style.overflowY = "auto"

        navigate(`/`)
        // navigate(-1)
      }
    }

  return (
    <motion.div variants={shadowAnim} animate = "visible"
    initial = "hidden" className='rshadow' onClick={exitDetailHandler}>
        <motion.div variants={containerAnim}
        animate = "visible"
        initial = "hidden" className="rcontainer">
        </motion.div>
        <form action="#">
      <label for="lang">Language</label>
      <select name="languages" id="lang">
        <option value="select">Select a Language</option>
        <option value="javascript">JavaScript</option>
        <option value="php">PHP</option>
        <option value="java">Java</option>
        <option value="golang">Golang</option>
        <option value="python">Python</option>
        <option value="c#">C#</option>
        <option value="C++">C++</option>
        <option value="erlang">Erlang</option>
      </select>
      <input type="submit" value="Submit" />
</form>
    </motion.div>
  )
}

export default Reviews
