import axios from "axios"
import {
    SONG_LIST_REQUEST,
    SONG_LIST_SUCCESS,
    SONG_LIST_FAIL,

    SONG_DETAILS_REQUEST,
    SONG_DETAILS_SUCCESS,
    SONG_DETAILS_FAIL,
} from "../constants/songConstants"

export const listSongs = () => async (dispatch) => {
    try {

        dispatch({
            type: SONG_LIST_REQUEST
        })

        const {
            data
        } = await axios.get(`/api/songs/`)
        dispatch({
            type: SONG_LIST_SUCCESS,
            payload: data
        })
    } catch (error) {
        dispatch({
            type: SONG_LIST_FAIL,
            payload: error.responce && error.responce.data.message ? error.responce.data.message : error.message
        })
    }
}
export const listSongDetails = (id) => async (dispatch) => {
    try {

        dispatch({
            type: SONG_DETAILS_REQUEST
        })

        const {
            data
        } = await axios.get(`/api/songs/${id}`)
        dispatch({
            type: SONG_DETAILS_SUCCESS,
            payload: data
        })
    } catch (error) {
        dispatch({
            type: SONG_DETAILS_FAIL,
            payload: error.responce && error.responce.data.message ? error.responce.data.message : error.message
        })
    }
}
