import React, {
  useState,
  useEffect
} from 'react'
import {
  motion
} from 'framer-motion'
import {
  useNavigate
} from 'react-router-dom'
import {
  containerAnim,
  shadowAnim
} from '../animations'
import {
  useDispatch,
  useSelector
} from 'react-redux'
import {
  login,
  register
} from '../actions/userActions'
import Loader from './Loader'
import Message from './Message'
import MessageR from './MessageR'


const Profile = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [message, setMessage] = useState(null)
    const [logReg, setLogReg] = useState(true)
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [confirmPassword, setConfirmPassword] = useState("")
    const [errormes, setErrormes] = useState(true)
    const userDetails = useSelector(state => state.userDetails)
    const {
      userInfo,
      loading,
      error
    } = userDetails
    document.body.style.overflow = "hidden"

    window.addEventListener('popstate', function (event) {
      // document.body.style.overflow = "auto"

    })

    const exitDetailHandler = (e) => {
      const element = e.target

      if (element.classList.contains("rshadow")) {
        document.body.style.overflowY = "auto"

        navigate(`/`)
        // navigate(-1)
      }
    }

    return ( <
      motion.div variants = {
        shadowAnim
      }
      animate = "visible"
      initial = "hidden"
      className = 'pshadow'
      onClick = {
        exitDetailHandler
      } >
      <
      motion.div variants = {
        containerAnim
      }
      animate = "visible"
      initial = "hidden"
      className = "pcontainer" >
      <
      form className = {
        `cont`
      } > {
        loading && < Loader / >
      } {
        message && < MessageR color = {
          "white"
        }
        errormes = {
          errormes
        }
        setErrormes = {
            setErrormes
          } > {
            message
          } < /MessageR>} <
          div className = "lr-cont" >
          <
          div className = "form-group" >
          <
          label htmlFor = "name" > Name: < /label> <
          input required placeholder = 'Enter Name'
        type = "text"
        name = 'name'
        id = "name"
        value = {
          name
        }
        onChange = {
          (e) => setName(e.target.value)
        }
        /> <
        /div> <
        div className = "form-group" >
          <
          label htmlFor = "email" > Email: < /label> <
          input required placeholder = 'Enter Email'
        type = "email"
        name = 'email'
        id = "email"
        value = {
          email
        }
        onChange = {
          (e) => setEmail(e.target.value)
        }
        /> <
        /div> <
        div className = "form-group" >
          <
          label htmlFor = "password" > Password: < /label> <
          input required placeholder = 'Enter Password'
        type = "password"
        name = 'password'
        id = "password"
        value = {
          password
        }
        onChange = {
          (e) => setPassword(e.target.value)
        }
        /> <
        /div> <
        div className = "form-group" >
          <
          label htmlFor = "password" > Confirm Password: < /label> <
          input required placeholder = 'Confirm Password'
        type = "password"
        name = 'password'
        id = "password"
        value = {
          confirmPassword
        }
        onChange = {
          (e) => setConfirmPassword(e.target.value)
        }
        /> <
        /div> <
        /div> <
        button className = 'reg-btn'
        type = "submit"
        value = "REGISTER" > REGISTER < span className = 'weve' > < /span></button >
          <
          /form> <
          /motion.div> <
          /motion.div>
      )
    }

    export default Profile
