import React, { useState, useEffect} from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {faPlay, faAngleLeft, faAngleRight, faPause} from "@fortawesome/free-solid-svg-icons"

const Player = ({currentSong, isplaying, setIsplaying, audioRef, songs, setCurrentSong, setSongs}) => {
    const playSongHandler = () =>{
        if(isplaying){
            audioRef.current.pause()
            setIsplaying(!isplaying) 
        }
        else{
            audioRef.current.play()
            setIsplaying(!isplaying) 
        }

    }
    const [songInfo, setSonfInfo] = useState({
        currentTime: null,
        duration: null,
        animationPercantage: 0,
    })
    const timeUpdateHandler = (e) => {
        const current = e.target.currentTime
        const duration = e.target.duration
        const roundedCurrent = Math.round(current)
        setSonfInfo({...songInfo, currentTime:current, duration:duration})

    }
    const getTime = (time) =>{
            return Math.floor(time / 60) + ":" + ("0" + Math.floor(time%60)).slice(-2)

    }
    const dragHandler = (e) => {
        setSonfInfo({...songInfo, currentTime: e.target.value})
        audioRef.current.currentTime = e.target.value
    }
    const skipTrackHandler = (direction) => {
        let currentIndex = songs.findIndex((song)=>song.id === currentSong.id)
        if(direction ==="skip-forward"){
            setCurrentSong(songs[(currentIndex+1)%songs.length])
        }
        if(direction ==="skip-back"){
            if((currentIndex-1)%songs.length ===-1){
                currentIndex = songs.length
            }
            setCurrentSong(songs[(currentIndex-1)%songs.length])
        }
    }
    useEffect(()=>{

        const newSongs = songs.map((song) => {
            if (song.id === currentSong.id) {
              return {
                ...song,
                active: true,
              };
            } else {
              return {
                ...song,
                active: false,
              };
            }
          });
          setSongs(newSongs);
    },[currentSong])
    if(isplaying){
        const playPromise = audioRef.current.play()
        if(playPromise !==undefined){
            playPromise.then((audio) => {
                audioRef.current.play()
            })
        }
    }
  return (
    <div className='player'>
        <div className='time-control'>
            <p>{getTime(songInfo.currentTime)}</p>
            <div className="track">
            <input  onChange={dragHandler} min={0} max={songInfo.duration ? songInfo.duration : ""} value={songInfo.currentTime ? songInfo.currentTime : 0} type="range"/>
            <div className="animate-track">
            </div>
            </div>
            <p> &nbsp;{getTime(songInfo.duration)}</p>
        </div>
        <div className="play-control">
            <FontAwesomeIcon onClick={()=>skipTrackHandler("skip-back")} className='skip-back' size='2x' icon={faAngleLeft} />
            <FontAwesomeIcon onClick={playSongHandler} className='play' size='2x' icon={isplaying ? faPause : faPlay} />
            <FontAwesomeIcon onClick={()=>skipTrackHandler("skip-forward")} className='skip-forward' size='2x' icon={faAngleRight} />
        </div>
        <audio onLoadedMetadata={timeUpdateHandler} onTimeUpdate={timeUpdateHandler} ref={audioRef} src={currentSong.audio}></audio>
    </div>
  )
}

export default Player
