import React from 'react'
import {
  motion
} from 'framer-motion'
import {
  useNavigate
} from 'react-router-dom'
import {
  containerAnim,
  shadowAnim
} from '../animations'


const Reviews = () => {
  const navigate = useNavigate()
  document.body.style.overflow = "hidden"

  window.addEventListener('popstate', function (event) {
    // document.body.style.overflow = "auto"

  })

  const exitDetailHandler = (e) => {
    const element = e.target

    if (element.classList.contains("rshadow")) {
      document.body.style.overflowY = "auto"

      navigate(`/`)
      // navigate(-1)
    }
  }

  return ( <
    motion.div variants = {
      shadowAnim
    }
    animate = "visible"
    initial = "hidden"
    className = 'rshadow'
    onClick = {
      exitDetailHandler
    } >
    <
    motion.div variants = {
      containerAnim
    }
    animate = "visible"
    initial = "hidden"
    className = "rcontainer" >
    <
    /motion.div> <
    /motion.div>
  )
}

export default Reviews
