import { useState, useRef, useEffect } from "react";
import Song from "./components/Song";
import Player from "./components/Player";
import "./styles/app.scss"
import { useSelector, useDispatch} from "react-redux"
import { listSongs } from "./actions/songActions";
import songss from "./data";
import Library from "./components/Library";
import Nav from "./components/Nav";
import Reviews from "./components/Reviews";
import Loader from "./components/Loader";
import Message from "./components/Message";
import Background from "./components/Background";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import Login from "./components/Login";
import SoundSlider from "./components/SoundSlider";
import CustomCursor from "./components/CustomCursor";
import axios from "axios"
function App() {
  const [errormes, setErrormes] = useState(false)
  console.log(errormes)
  const dispatch = useDispatch()
  const [width, setWidth] = useState(window.innerWidth)
  // const [songs, setSongs] = useState(
  //   [{_id: 3}]
  // )
  const songList = useSelector(state=>state.songList)
  let {error, loading, songs} = songList
  const [currentSong, setCurrentSong] = useState(songs[0])


  useEffect(()=>{


    const fetchSongs =  async () => {
      const {data} = await axios.get(`/api/songs/`)
      await dispatch(listSongs())
      await setCurrentSong(data[0])
    }
    fetchSongs()
  },[dispatch, ])

  const [isplaying, setIsplaying] = useState(false)
  const [libraryStatus, setLibraryStatus ] = useState(false)
  const audioRef = useRef(null)

  return (
    <BrowserRouter className={`App ${libraryStatus && "library-active"}`}>
      {<Message errormes={errormes} setErrormes={setErrormes}/>}
      {/* <Loader/> */}
      <CustomCursor />
      <Background/>
      <SoundSlider audioRef={audioRef}/>
      <Nav libraryStatus={libraryStatus} setLibraryStatus={setLibraryStatus}/>
      <Song libraryStatus={libraryStatus} isplaying={isplaying} currentSong={currentSong}/>
      <Player libraryStatus={libraryStatus} currentSong={currentSong} isplaying={isplaying} setIsplaying={setIsplaying} audioRef={audioRef} songs={songs} setCurrentSong={setCurrentSong}/>
    <Routes>
      <Route path="/login" element={<Login />}/>
      <Route path="/reviews" element={<Reviews/>}/>
      <Route path="/" element={<Library width={width} setWidth={setWidth} setLibraryStatus={setLibraryStatus} libraryStatus={libraryStatus} setCurrentSong={setCurrentSong} songs={songs} audioRef={audioRef} isplaying={isplaying} />}/>


    </Routes>

    </BrowserRouter>
  );
}

export default App;
