import React  from 'react'
import { useSelector, useDispatch} from "react-redux"
import { listSongDetails } from '../actions/songActions'
const LibrarySong = ({song,setCurrentSong,songs, audioRef, isplaying, id}) => {
    const dispatch = useDispatch()
    const songDetails = useSelector(state => state.songDetails)
    let {currentSong} = songDetails
    const fetchSong =  async (idd) => {
          await dispatch(listSongDetails(idd))
    }
    const songSelectHandler = () => {
        const selectedSong = songs.find(state=>state._id ===song._id)

        setCurrentSong(selectedSong)
        const newSongs = songs.map((song) => {
            if (song._id === id) {
              return {
                ...song,
                active: true,
              };
            } else {
              return {
                ...song,
                active: false,
              };
            }
          });
          songs = (newSongs);
        if(isplaying){
            const playPromise = audioRef.current.play()
            if(playPromise !==undefined){
                playPromise.then((audio) => {
                    audioRef.current.play()
                })
            }
        }
    }
  return (
    <div className={`library-song ${song.active ? "selected" : "l"}`} onClick={songSelectHandler}>
        <img src={song.cover} alt={song.name} />
        <div className="song-description"><h3>{song.name}</h3>
        <h4>{song.artist}</h4></div>
    </div>
  )
}

export default LibrarySong
