import { useState, useRef } from "react";
import Song from "./components/Song";
import Player from "./components/Player";
import "./styles/app.scss"
import data from "./data"
import Library from "./components/Library";
import Nav from "./components/Nav";
import Reviews from "./components/Reviews";
import Loader from "./components/Loader";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import Login from "./components/Login";
import SoundSlider from "./components/SoundSlider";
import CustomCursor from "./components/CustomCursor";
function App() {
  const [width, setWidth] = useState(window.innerWidth)

  const [songs, setSongs] = useState(data())
  const [currentSong, setCurrentSong] = useState(songs[0])
  const [isplaying, setIsplaying] = useState(false)
  const [libraryStatus, setLibraryStatus ] = useState(false)
  const audioRef = useRef(null)

  return (
    <BrowserRouter className={`App ${libraryStatus && "library-active"}`}>

      {/* <Loader/> */}
      <CustomCursor/>
      <SoundSlider audioRef={audioRef}/>
      <Nav libraryStatus={libraryStatus} setLibraryStatus={setLibraryStatus}/>
      <Song libraryStatus={libraryStatus} isplaying={isplaying} currentSong={currentSong}/>
      <Player libraryStatus={libraryStatus} currentSong={currentSong} isplaying={isplaying} setIsplaying={setIsplaying} audioRef={audioRef} songs={songs} setCurrentSong={setCurrentSong} setSongs={setSongs}/>
    <Routes>
      <Route path="/login" element={<Login/>}/>
      <Route path="/reviews" element={<Reviews/>}/>
      <Route path="/" element={<Library width={width} setWidth={setWidth} setLibraryStatus={setLibraryStatus} libraryStatus={libraryStatus} setCurrentSong={setCurrentSong} songs={songs} audioRef={audioRef} isplaying={isplaying} setSongs={setSongs}/>}/>


    </Routes>

    </BrowserRouter>
  );
}

export default App;
